import React, { useRef } from 'react';

function App() {
 const userInput=useRef(null)
 let task =userInput.current.value;
  const addtask =(e)=>{
    alert("task added")
    e.preventDefault()
    alert(task)
  }
  return (
    <div className="container">
    <h1>Todo List</h1>
    <div className="header text-center">
      
     <div className="container">
      <form className="inputs"onSubmit={"add task"}>
      <input type="text"ref={userInput}placeholder='add todo item'></input>
      <input type='submit'value={"add"}></input>
      </form>
      <div className='List Wrapper'></div>
      <ul>
          <li>go to the home</li>
        </ul>
        <div className='icon position'>
          i className='<i class="fa-regular fa-circle-check"></i>'
          i className='<i class="fa-regular fa-pen-to-square"></i>'
          i className='<i class="fa-regular fa-pen-to-square"></i>'
        </div>
    </div>
    </div>
    </div>
  );
}

export default App;
