import { useState } from 'react';
import './App.css';

function App() {
  const [list, setList] = useState([
    { id: 1, task: 'play', done: false }
  ]);
  const [task, setTask] = useState('');

  function Add() {
    const newObj = { id: 2, task: task, done: false };
    setList(prevList => [...prevList, newObj]);
  }

  function update(id) {
    const newList = list.map(item => (item.id === id ? { ...item, done: true } : item));
    setList(newList);
  }

  return (
    <div className="container">
      <h1>Todo List</h1>
      <div className="header text-center">
        <div className="container">
          <div className='inputpart'>
            <input type='text' placeholder='add task' onChange={e => setTask(e.target.value)} />
            <button className='addBtn' onClick={Add}>Add</button>
          </div>
          <div className='taskList'>
            <ul>
              {list.map((item) => (
                <li key={item.id} onClick={() => update(item.id)} className={item.done ? "done" : ""}>
                  <span>{item.task}</span>
                  <span>x</span>
                </li>
              ))}
            </ul>
            <div className='icon position'>
              <i className='far fa-circle-check'></i>
              <i className='far fa-pen-to-square'></i>
              <i className='far fa-pen-to-square'></i>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
